import React, { useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import { Box, Button, TextField, Typography } from "@mui/material";
import axios from "axios";

const SignupPage = () => {
  const { login } = useContext(AuthContext);
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    role: "user", // Default role
  });

  const [error, setError] = useState("");

  // Handle input change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle Signup Submission
  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/auth/signup", formData);
      login({ token: response.data.token, email: formData.email });
      navigate("/"); // Redirect to HomePage
    } catch (err) {
      setError(err.response?.data?.msg || "Signup failed. Try again.");
    }
  };

  return (
    <Box sx={{ width: "300px", margin: "auto", mt: 5, textAlign: "center" }}>
      <Typography variant="h5">Sign Up</Typography>
      {error && <Typography color="red">{error}</Typography>}
      <TextField
        label="Name"
        name="name"
        fullWidth
        sx={{ mt: 2 }}
        value={formData.name}
        onChange={handleChange}
      />
      <TextField
        label="Email"
        name="email"
        type="email"
        fullWidth
        sx={{ mt: 2 }}
        value={formData.email}
        onChange={handleChange}
      />
      <TextField
        label="Password"
        name="password"
        type="password"
        fullWidth
        sx={{ mt: 2 }}
        value={formData.password}
        onChange={handleChange}
      />
      <Button variant="contained" sx={{ mt: 2 }} onClick={handleSignup}>
        Sign Up
      </Button>
    </Box>
  );
};

export default SignupPage;
