import { useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";

const AuthPage = () => {
    const { handleSignup, handleLogin } = useContext(AuthContext);
    const [form, setForm] = useState({ name: "", email: "", password: "", isLogin: true });

    const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (form.isLogin) {
            await handleLogin({ email: form.email, password: form.password });
        } else {
            await handleSignup({ name: form.name, email: form.email, password: form.password, role: "user" });
        }
    };

    return (
        <div>
            <h2>{form.isLogin ? "Login" : "Signup"}</h2>
            <form onSubmit={handleSubmit}>
                {!form.isLogin && (
                    <input type="text" name="name" placeholder="Name" value={form.name} onChange={handleChange} required />
                )}
                <input type="email" name="email" placeholder="Email" value={form.email} onChange={handleChange} required />
                <input type="password" name="password" placeholder="Password" value={form.password} onChange={handleChange} required />
                <button type="submit">{form.isLogin ? "Login" : "Signup"}</button>
            </form>
            <button onClick={() => setForm({ ...form, isLogin: !form.isLogin })}>
                {form.isLogin ? "Switch to Signup" : "Switch to Login"}
            </button>
        </div>
    );
};

export default AuthPage;
