import React, { useContext } from "react";
import { Link, useLocation } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import { AppBar, Toolbar, Typography, Button, Box } from "@mui/material";

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const location = useLocation();

  const isActive = (path) => location.pathname === path;

  return (
    <AppBar position="static" sx={{ background: "#2E3B55" }}>
      <Toolbar>
        <Typography variant="h6" sx={{ flexGrow: 1 }}>
          MedShield
        </Typography>

        <Box sx={{ display: "flex", gap: 2 }}>
          <Button
            component={Link}
            to="/"
            sx={{
              color: isActive("/") ? "yellow" : "white",  // Highlight active tab
              fontWeight: isActive("/") ? "bold" : "normal",
            }}
          >
            Home
          </Button>

          {user ? (
            <>
              {user.role === "user" && (
                <>
                  <Button
                    component={Link}
                    to="/medicine-verification"
                    sx={{
                      color: isActive("/medicine-verification") ? "yellow" : "white",
                      fontWeight: isActive("/medicine-verification") ? "bold" : "normal",
                    }}
                  >
                    Scan Medicine
                  </Button>
                  <Button
                    component={Link}
                    to="/my-prescriptions"
                    sx={{
                      color: isActive("/my-prescriptions") ? "yellow" : "white",
                      fontWeight: isActive("/my-prescriptions") ? "bold" : "normal",
                    }}
                  >
                    My Prescriptions
                  </Button>
                  <Button
                    component={Link}
                    to="/report-fraud"
                    sx={{
                      color: isActive("/report-fraud") ? "yellow" : "white",
                      fontWeight: isActive("/report-fraud") ? "bold" : "normal",
                    }}
                  >
                    Report Fraud
                  </Button>
                  <Button
                    component={Link}
                    to="/best-stores"
                    sx={{
                      color: isActive("/best-stores") ? "yellow" : "white",
                      fontWeight: isActive("/best-stores") ? "bold" : "normal",
                    }}
                  >
                    Best Stores
                  </Button>
                </>
              )}

              {user.role === "doctor" && (
                <Button
                  component={Link}
                  to="/upload-prescription"
                  sx={{
                    color: isActive("/upload-prescription") ? "yellow" : "white",
                    fontWeight: isActive("/upload-prescription") ? "bold" : "normal",
                  }}
                >
                  Add Prescription
                </Button>
              )}

              {user.role === "pharmacist" && (
                <Button
                  component={Link}
                  to="/pharmacy-access"
                  sx={{
                    color: isActive("/pharmacy-access") ? "yellow" : "white",
                    fontWeight: isActive("/pharmacy-access") ? "bold" : "normal",
                  }}
                >
                  View Prescriptions
                </Button>
              )}

              <Button color="error" onClick={logout}>
                Logout
              </Button>
            </>
          ) : (
            <Button
              component={Link}
              to="/auth"
              sx={{
                color: isActive("/auth") ? "yellow" : "white",
                fontWeight: isActive("/auth") ? "bold" : "normal",
              }}
            >
              Login
            </Button>
          )}
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
