import React, { useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";
import { Box, Button, TextField, Typography, MenuItem, Select, FormControl, InputLabel } from "@mui/material";

const SignupPage = () => {
  const { login } = useContext(AuthContext);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("user"); // Default role
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();
    setError(null);

    try {
      const response = await fetch("http://localhost:5000/api/auth/signup", { // Adjust URL if needed
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ name, email, password, role }),
      });

      const data = await response.json();

      if (response.ok) {
        login({ token: data.token }); // Store token in context
        navigate("/"); // Redirect to homepage
      } else {
        setError(data.msg || (data.errors ? data.errors[0].msg : "Signup failed"));
      }
    } catch (error) {
      console.error("Signup Error:", error);
      setError("Something went wrong! Please try again.");
    }
  };

  return (
    <Box sx={{ width: "300px", margin: "auto", mt: 5, textAlign: "center" }}>
      <Typography variant="h5">Signup</Typography>

      {error && <Typography color="error">{error}</Typography>}

      <TextField
        label="Name"
        fullWidth
        sx={{ mt: 2 }}
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <TextField
        label="Email"
        type="email"
        fullWidth
        sx={{ mt: 2 }}
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <TextField
        label="Password"
        type="password"
        fullWidth
        sx={{ mt: 2 }}
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />

      <FormControl fullWidth sx={{ mt: 2 }}>
        <InputLabel>Role</InputLabel>
        <Select value={role} onChange={(e) => setRole(e.target.value)}>
          <MenuItem value="user">User</MenuItem>
          <MenuItem value="pharmacy">Pharmacy</MenuItem>
          <MenuItem value="admin">Admin</MenuItem>
        </Select>
      </FormControl>

      <Button variant="contained" sx={{ mt: 2 }} onClick={handleSignup}>
        Signup
      </Button>
      <Button sx={{ mt: 2 }} onClick={() => navigate("/auth")}>
        Already have an account? Login
      </Button>
    </Box>
  );
};

export default SignupPage;
