import React, { useState } from "react";
import { Container, Typography, Button, TextField } from "@mui/material";

const PharmacyAccessPage = () => {
  const [userId, setUserId] = useState("");

  const handleScan = () => {
    if (!userId) {
      alert("Please enter or scan the user ID!");
      return;
    }
    console.log("Retrieving prescription for User ID:", userId);
    // TODO: Fetch prescription details from the backend
  };

  return (
    <Container maxWidth="sm" style={{ marginTop: "50px", textAlign: "center" }}>
      <Typography variant="h5">Pharmacy Prescription Access</Typography>
      <TextField
        label="Enter or Scan User ID"
        variant="outlined"
        fullWidth
        value={userId}
        onChange={(e) => setUserId(e.target.value)}
        style={{ margin: "20px 0" }}
      />
      <Button variant="contained" color="primary" onClick={handleScan}>
        Retrieve Prescription
      </Button>
    </Container>
  );
};

export default PharmacyAccessPage;
